import React, { Component } from 'react';
// import logo from './logo.svg';
import './App.css';

class App extends Component {

  constructor(props){
    super(props);
    this.state = {
      operator: false,
      overlap: false,
      is_first_num: true,
      is_float: false,
      saved: 0, // (1st number or total number)
      displayed: 0, // (2st number)
      under_point: 0,
      is_under_first_num: true,
      pow_num: 0
    }
  }

  clickNum = (num) => () => {
    let overlap = this.state.overlap;
    let operator = this.state.operator;
    let displayed = this.state.displayed;
    let is_first_num = this.state.is_first_num;
    let saved = this.state.saved;
    let is_float = this.state.is_float;
    let under_point = this.state.under_point;
    let is_under_first_num = this.state.is_under_first_num;
    let pow_num = this.state.pow_num;


    //for 소수점 위//
    if(is_float === false) {
      if (operator === false) {
        if (is_first_num === true) { // [CASE 1] 연산자: 없음, 첫번째 숫자타이핑: Y
          is_first_num = false;
          displayed = num;
        } else { // [CASE 2] 연산자: 없음, 첫번째 숫자: N
//          if (this.state.value < Math.pow(2, 49)) {}
          displayed = displayed * 10 + num;
        }
      } else {
        if (is_first_num === true) { // [CASE 3] 연산자: 있음, 첫번째 숫자타이핑: Y
          overlap = false;
          saved = displayed;
          displayed = num;
          is_first_num = false;
        } else { // [CASE 4] 연산자: 있음, 첫번째 숫자타이핑: N
          displayed = displayed * 10 + num;
        }
      }
    }


    //for 소수점 아래//
    else { //시작
      pow_num += 1; //몇번째 소수점인지에 대한 숫자.

      if (operator === false) {
        if (is_under_first_num === true) { // [CASE 1] 연산자: 없음, 첫번째 소수 타이핑: Y
          is_under_first_num = false;
          under_point = num * 0.1;
          displayed = displayed + under_point;
        } else { // [CASE 2] 연산자: 없음, 첫번째 소수 타이핑: N
          under_point = num * Math.pow(0.1, pow_num);
          displayed = displayed + under_point;
        }
      } else {
        if (is_under_first_num === true) { // [CASE 3] 연산자: 있음, 첫번째 소수 타이핑: Y
          is_under_first_num = false;
          overlap = false;
          under_point = num * 0.1;
          displayed = displayed + under_point;
          saved = displayed;
        } else { // [CASE 4] 연산자: 있음, 첫번째 소수 타이핑: N
          under_point = num * Math.pow(0.1, pow_num);
          displayed = displayed + under_point;
        }
      } //끝
    }


    this.setState({
      overlap: overlap,
      operator: operator,
      displayed: displayed,
      is_first_num: is_first_num,
      saved: saved,
      is_float: is_float,
      under_point: under_point,
      is_under_first_num: is_under_first_num,
      pow_num: pow_num
      });
    }

  refreshValue = () => () => {
    this.setState({
      operator: false,
      overlap: false,
      is_first_num: true,
      saved: 0,
      displayed: 0
    });
  }

  clickOper = (oper) => () => {
    let overlap = this.state.overlap;
    let operator = this.state.operator;
    let saved = this.state.saved;
    let displayed = this.state.displayed;
    let is_first_num = this.state.is_first_num;
    let is_float = this.state.is_float;
    let pow_num = this.state.pow_num;

    if (overlap === false) { //중복이 아닌, 첫번째 연산자 입력일 경우
      if (operator === '+') {
        saved = saved + displayed;
        displayed = saved;
      } else if (operator === '-') {
        saved = saved - displayed;
        displayed = saved;
      } else if (operator === '*') {
        saved = saved * displayed;
        displayed = saved;
      } else if (operator === '/') {
        saved = saved / displayed;
        displayed = saved;
      }
    }

    is_first_num = true;
    overlap = true;

    if (oper !== '=') {
      operator = oper;
      is_float = false;
      pow_num = 0;
    }


    this.setState({
      overlap: overlap,
      operator: operator,
      saved: saved,
      displayed: displayed,
      is_first_num: is_first_num,
      is_float: is_float,
      pow_num: pow_num
    });


  }



  clickPoint = () => () => {
    let is_float = this.state.is_float;

    is_float = true;

    this.setState({
      is_float: is_float
    });
  }


  render() {
    return (
        <div className="App">

          <div className="same">
            <div className="typing"> {this.state.displayed}</div>
            <div className="caltool">
              <div className="flex">
                <div onClick={this.refreshValue()} className="first_three">AC</div>
                <div onClick={this.clickOper("/")} className="first_one">/</div>
              </div>

              <div className="flex">
                <div onClick={this.clickNum(7)} className="rest_one">7</div>
                <div onClick={this.clickNum(8)} className="rest_one">8</div>
                <div onClick={this.clickNum(9)} className="rest_one">9</div>
                <div onClick={this.clickOper("*")} className="rest_one">*</div>
              </div>

              <div className="flex">
                <div onClick={this.clickNum(4)} className="rest_one">4</div>
                <div onClick={this.clickNum(5)} className="rest_one">5</div>
                <div onClick={this.clickNum(6)} className="rest_one">6</div>
                <div onClick={this.clickOper("-")} className="rest_one">-</div>
              </div>

              <div className="flex">
                <div onClick={this.clickNum(1)} className="rest_one">1</div>
                <div onClick={this.clickNum(2)} className="rest_one">2</div>
                <div onClick={this.clickNum(3)} className="rest_one">3</div>
                <div onClick={this.clickOper("+")} className="rest_one">+</div>
              </div>

              <div className="flex">
                <div onClick={this.clickNum(0)} className="rest_two">0</div>
                <div onClick={this.clickPoint()} className="rest_one">.</div>
                <div onClick={this.clickOper("=")} className="rest_one">=</div>
              </div>
            </div>
          </div>

        </div>
    );
  }
}

export default App;
